sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var ga = new GlideAjax('AssignmentGroupUtils');
	ga.addParam('sysparm_name','assignAnalyst');
	ga.addParam('sysparm_group_id',g_form.getValue('assignment_group'));	
	ga.getXML(membersParse);

	function membersParse(response){
		var obj = response.responseXML.documentElement.getAttribute('answer');
		var members = JSON.parse(obj);

		if (members.length > 0){
			var randomNum = Math.floor(Math.random() * members.length);
			g_form.setValue('assigned_to',members[randomNum].sys_id, members[randomNum].name);
		}
		else{
			g_form.setValue('assigned_to','');
			alert('The assignment group has no users assigned to it');
		}
	}
}
