sys_script_include.script:
var AssignmentGroupUtils = Class.create();
AssignmentGroupUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	countGroupMembers: function(){
		var msg = 'The group has no members';
		var groupId = this.getParameter('sysparm_group_id');

// 		gs.debug('ap: ' +groupId);

		var gr = new GlideRecord('sys_user_grmember');
		//gr.get(groupId);
		gr.addQuery('group.sys_id',groupId);
		gr.query();		

		if (gr.next()){
			var grpMembersCount = gr.getRowCount();
			var grpName = gr.getDisplayValue('group');
			msg = "The group '" + grpName + "' has " + grpMembersCount + ' members';			
		}

		return msg;
	},

	type: 'AssignmentGroupUtils'
});
