sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// Get request data

	var body = request.body.data;

	// Handle input that for this test is expected to be "firstname lastname" 
	var nameParts = body.name.split(' ');	
	var userName = nameParts[0][0] + nameParts[1][0];
	userName = userName.toLowerCase();
	var newUserValues = {
		firstname: nameParts[0],
		lastname:nameParts[1],
		username: userName,
		email: userName + '@fabrikam.com'
	};

	// Get or create user
	var gr = new GlideRecord("sys_user");
	gr.addQuery('user_name', userName);
	gr.query();

	var userId;
	if (!gr.next()) {
		// Create user
		gr.user_name = newUserValues.username;
		gr.first_name = newUserValues.firstname;
		gr.last_name = newUserValues.lastname;
		gr.email = newUserValues.email;
		userId = gr.insert();
	}
	else {
		userId = gr.sys_id.getDisplayValue();
	}

	// Create Incident with the new user as the caller
	var grInc = new GlideRecord('incident');
	grInc.newRecord();
	grInc.caller_id = userId;
	grInc.short_description = "Test incident created for '" + body.name + "'";
	var incidentId = grInc.insert();

	// Get newly created incident record
	grInc.initialize();
	grInc.get(incidentId);
	grInc.query();
	grInc.next();

	// Escalate problem
	var grPrb = new GlideRecord("problem");
	grPrb.short_description = grInc.short_description;
	grPrb.cmdb_ci = grInc.cmdb_ci;
	grPrb.impact = grInc.impact;
	grPrb.urgency = grInc.urgency;
	grPrb.priority = grInc.priority;
	grPrb.company = grInc.company;
	grPrb.sys_domain = grInc.sys_domain;
	var probId = grPrb.insert();

	grInc.problem_id = probId;
	grInc.update();

	return {
		'user displayname': body.name,
		username : userName,
		'incident number' : grInc.getDisplayValue('number'),
		'problem number': grPrb.getDisplayValue('number')
	};

})(request, response);
